package jc01.homework.lesson04.training;

public class Task040506 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 3;
		int b = 4;
		
		if (a == b) {
			System.out.println("Числа a и b - равны");
		}
		
		else {
			System.out.println("Числа a и b - не равны");			
		}
		
		if (a < b) {
			System.out.println("Наименьшее из чисел - a = " + a);
		}
		
		else {
			System.out.println("Наименьшее из чисел - b = " + b);
		}
		
		if (a > b) {
			System.out.println("Наибольшее из чисел - a = " + a);
		}
		
		else {
			System.out.println("Наибольшее из чисел - b = " + b);
		}
	}

}
